import React, { useEffect, useState } from 'react'
import '../CSS/Work.css'
import axios from 'axios'
import { Link } from "react-router-dom";
import WorkDetails from './work_details/WorkDetails';



function WorkPage() {
    const MyContext = React.createContext();
    var [worklists, setWorklists] = useState([])
    var linkStyle = { textDecoration: 'none' }
    function fetchData() {
        axios.get("http://localhost:5100/WorkList")
            .then(response => {

                setWorklists(response.data);
            })
            .catch(error => {
                console.log(error);

            })
    }

    useEffect(() => {
        fetchData();
    })

    return (

        <div className="work-page">

            {worklists.map((item) =>
                <Link to="/work/workdetails"  style={linkStyle}>
                    <MyContext.Provider>
                        
                    </MyContext.Provider>
                    <div className="item">
                        <div className="items-image" style={{ backgroundImage: "url(" + item.images[1] + ")", backgroundSize: 'cover', backgroundRepeat: 'no-repeat' }}></div>
                        <p className="items-title">{item.title}</p>
                    </div>
                </Link>

            )}

        </div>


    )
}

export default WorkPage
